public class PortletDelegatingPhaseListenerMulticaster extends
		DelegatingPhaseListenerMulticaster {



	/**
	 * 
	 */
	private static final long serialVersionUID = -2897100522007379793L;
	private static final Object FLOW_EXECUTION_HOLDER = "FLOW_EXECUTION_HOLDER";

	public void beforePhase(PhaseEvent event) {
		FlowExecutionHolder holder = (FlowExecutionHolder) event.getFacesContext().getExternalContext().getSessionMap().get(FLOW_EXECUTION_HOLDER);
		if (holder != null) {
			FlowExecutionHolderUtils.setFlowExecutionHolder(holder,event.getFacesContext());
		}
		super.beforePhase(event);
	}



	public void afterPhase(PhaseEvent event) {
			FlowExecutionHolder holder = FlowExecutionHolderUtils.getFlowExecutionHolder(event.getFacesContext());
			event.getFacesContext().getExternalContext().getSessionMap().put(FLOW_EXECUTION_HOLDER,holder);
			super.afterPhase(event);
	}
}

