where I've defined the following functions:

function submitEvent(source, eventId, params) {
  var form = findContainingForm(source);

  addOrUpdateField(form, '_eventId', eventId);

  for(var param in params)
    addOrUpdateField(form, param, params[param]);

  form.submit();
}

function findContainingForm(target) {
  while(target && !(target.nodeName == "FORM")) {
    target = target.parentNode;
  }
  return target;
}

function addOrUpdateField(form, name, value) {
  var field = form.elements[name];
  if(field != null)
    field.disabled = true; // this should probably iterate through and disable
  if(value.constructor == Array) {
    for(var i = 0; i < value.length; ++i) {
      var input = document.createElement('input');
      input.type = 'hidden';
      input.name = name;
      input.value = value[i];
      form.appendChild(input);
	}
  }
  else {
    var input = document.createElement('input');
    input.type = 'hidden';
    input.name = name;
    input.value = value;
    form.appendChild(input);
  }
}

